  const navigation = useNavigation();

  const goToCategoria = () => {
    navigation.navigate('Categorias'); // 'Categoria' debe coincidir con el nombre de la pantalla en Stack.Navigator
  };

  return (
    <View style={styles.container}>
      <Button title="Ir a CategorÃ­a" onPress={goToCategoria} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
});

export default HomeScreen;
